mapper.update:
UPDATE ITEM SET
    WACTR_CD = #{wactrCd}
  , CLNT_CD = #{clntCd}
  , LOC_CD = #{locCd}
  , ITEM_CD = #{itemCd}
  , ITEM_NM = #{itemNm}
  , BOX_UNIT = #{boxUnit}
  , PLT_IN_BOX = #{pltInBox}
  , REMK = #{remk}
  , MOD_DT = SYSDATE
  , MOD_NM = #{modNm}
WHERE WACTR_CD || CLNT_CD || LOC_CD || ITEM_CD = #{keyVal}

mapper.select:
SELECT ITEM_CD
FROM ITEM
WHERE ITEM_CD = #{itemCd}

mapper.update:
UPDATE ITEM
    SET DEL_YN = 'Y'
WHERE WACTR_CD || CLNT_CD || LOC_CD || ITEM_CD = #{keyVal}
