mapper.select:
SELECT NVL(MAX(RELS_NO), 0) + 1 RELS_NO
FROM RELS_H
WHERE RELS_DT = #{relsDt}

mapper.update:
UPDATE RELS_D
SET CUST_CD = #{custCd}
, CUST_CTR_CD = #{custCtrCd}
, ITEM_CD = #{itemCd}
, REG_IN_AMT = #{regInAmt}
, RELS_CNT = #{relsCnt}
, DELI_REQUEST_DT = #{deliRequestDt}
, MOD_DT = SYSDATE
, MOD_NM = #{modNm}
WHERE TO_CHAR(RELS_DT, 'yyyyMMdd') || RELS_NO || RELS_D_NO = #{keyVal}
AND DEL_YN = 'N'
AND ROWNUM = 1

UPDATE RELS_H
SET STATUS = '03'
WHERE TO_CHAR(RELS_DT, 'yyyyMMdd') || RELS_NO = #{keyVal}
