mapper.update:
UPDATE STDIN_H H SET H.STDIN_DT = #{stdinDt}, H.CLNT_CD = #{clntCd}, H.STATUS = #{status}, H.REMK = #{remark}, H.MOD_DT = SYSDATE, H.MOD_NM = #{modNm},
H.DEL_YN = 'Y' WHERE H.STDIN_NUM = #{stdinNum}
H.DEL_YN = 'N' WHERE H.STDIN_NUM = #{stdinNum}

UPDATE STDIN_D D SET D.STDIN_DT= #{stdinDt}, D.ITEM_CD = #{itemCd}, D.CLNT_CD = #{clntCd}, D.LOC_CD = #{locCd}, D.WACTR_CD = #{wactrCd}, D.BEFORE_STDIN = #{beforeStdin}, D.NORMAL = #{normal}, D.FAULT = #{fault}, D.REMK = #{remark}, D.MOD_DT = SYSDATE, D.MOD_NM = #{modNm},
D.DEL_YN = 'Y' WHERE D.STDIN_NUM = #{stdinNum}
D.DEL_YN = 'N' WHERE D.STDIN_NUM = #{stdinNum}

mapper.select:
SELECT * FROM STDIN_H H LEFT OUTER JOIN CLNT C ON H.CLNT_CD = C.CLNT_CD


		H.STDIN_DT = #{stdinDt}


		AND C.CLNT_NM LIKE '%'||#{clntNm}||'%'


		AND H.STATUS = #{status}
