mapper.select:
select l.* , w.wactr_nm from loc l
join wactr w on l.wactr_cd = w.wactr_cd
where loc_cd = #{loc_cd}
and l.wactr_cd = #{wactr_cd}

mapper.update:
update loc set del_yn = 'Y'
where loc_cd = #{loc_cd}
and wactr_cd = #{wactr_cd}

mapper.delete:
delete from loc
where
loc_cd = #{loc_cd}
and wactr_cd = #{wactr_cd}
